(function(e) {
    "use strict";
    e(function(){
        e("#dismiss-ajax-notification").length > 0 && e("#dismiss-ajax-notification").click(function(t){
            t.preventDefault();
            e.post(ajaxurl,{
                action:"hide_admin_notification",
                nonce:e.trim(e("#ajax-notification-nonce").text())
            },function(t){
                "1"===t?e("#ajax-notification").fadeOut("slow"):e("#ajax-notification").removeClass("updated").addClass("error")})})})})(jQuery);
